configuration.config:
   type Loaders is ("ROM_NS_lpc55s69", "RAM_NS_lpc55s69", "RAM", "USER");

   Loader : Loaders := external ("LOADER", "RAM_NS_lpc55s69");

   package Compiler is
      Common_Required_Switches := ("-mlittle-endian", "-mhard-float",
        "-mcpu=cortex-m4", "-mfpu=fpv4-sp-d16", "-mthumb", "-g");

      for Leading_Required_Switches ("Ada") use
         Compiler'Leading_Required_Switches ("Ada") &
         Common_Required_Switches;

      for Leading_Required_Switches ("C") use
         Compiler'Leading_Required_Switches ("C") &
         Common_Required_Switches;
   end Compiler;

   package Linker is
      for Required_Switches use Linker'Required_Switches &
        ("-L${RUNTIME_DIR(ada)}/adalib", "-nolibc", "-nostartfiles") &
        Compiler.Common_Required_Switches;

	case Loader is
	 when "ROM_NS_lpc55s69" =>
            for Required_Switches use Linker'Required_Switches &
            ("-L", "${RUNTIME_DIR(ada)}/../../bsps/lpc55s69_ns/bsp/",
             "-T", "memory-map_lpc55s692.ld",
             "-T", "common-" & Loader & ".ld");
	 when "RAM_NS_lpc55s69" =>
            for Required_Switches use Linker'Required_Switches &
            ("-L", "${RUNTIME_DIR(ada)}/../../bsps/lpc55s69_ns/bsp/",
             "-T", "memory-map_lpc55s69.ld",
             "-T", "common-" & Loader & ".ld");
         when "USER" =>
         when others =>
            for Required_Switches use Linker'Required_Switches &
            ("-L", "${RUNTIME_DIR(ada)}/../../bsps/lpc55s69_ns/bsp/",
             "-T", "memory-map-lpc55s69.ld",
             "-T", "common-" & Loader & ".ld");
     end case;
   end Linker;
